            "defaultValue": "AuditIfNotExists"
          }
        },
        "policyRule": {
          "if": {
            "allOf": [
              {
                "field": "type",
                "equals": "microsoft.Web/sites"
              },
              {
                "anyOf": [
                  {
                    "field": "kind",
                    "equals": "functionapp"
                  },
                  {
                    "field": "kind",
                    "equals": "functionapp,linux"
                  },
                  {
                    "field": "kind",
