        "policyType": "BuiltIn",
        "mode": "All",
        "description": "Remote debugging requires inbound ports to be opened on a web application.  Remote debugging should be turned off.",
        "metadata": {
          "category": "Security Center"
        },
        "parameters": {
          "effect": {
            "type": "String",
            "metadata": {
              "displayName": "Effect",
              "description": "Enable or disable the execution of the policy"
            },
            "allowedValues": [
              "AuditIfNotExists",
              "Disabled"
            ],
            "defaultValue": "AuditIfNotExists"
          }
        },
        "policyRule": {
          "if": {
            "allOf": [
              {
                "field": "type",
                "equals": "microsoft.Web/sites"
              },
              {
                "anyOf": [
                  {
                    "field": "kind",
                    "equals": "app"
                  },
                  {
                    "field": "kind",
                    "equals": "WebApp"
                  },
                  {
                    "field": "kind",
                    "equals": "app,linux"
                  },
                  {
                    "field": "kind",
                    "equals": "app,linux,container"
                  }
                ]
              }
            ]
          },
          "then": {
            "effect": "[parameters('effect')]",
            "details": {
              "type": "Microsoft.Security/complianceResults",
              "name": "RemoteDebuggingForWebApplication",
              "existenceCondition": {
                "field": "Microsoft.Security/complianceResults/resourceStatus",
                "in": [
                  "NotApplicable",
                  "OffByPolicy",
                  "Healthy"
                ]
              }
            }
          }
        }
      },
      "id": "/providers/Microsoft.Authorization/policyDefinitions/cb510bfd-1cba-4d9f-a230-cb0976f4bb71",
      "type": "Microsoft.Authorization/policyDefinitions",
      "name": "cb510bfd-1cba-4d9f-a230-cb0976f4bb71"
    },
    {
      "properties": {
        "displayName": "Audit Windows VMs in which the Administrators group does not contain only the specified members",
        "policyType": "BuiltIn",
        "mode": "All",
        "description": "This policy audits Windows virtual machines in which the Administrators group does not contain only the specified members. This policy should only be used along with its corresponding deploy policy in an initiative. For more information on Guest Configuration policies, please visit https://aka.ms/gcpol",
        "metadata": {
          "category": "Guest Configuration"
        },
        "policyRule": {
          "if": {
            "allOf": [
              {
                "field": "type",
                "equals": "Microsoft.GuestConfiguration/guestConfigurationAssignments"
