{
  "properties": {
    "displayName": "Audit OS vulnerabilities on your virtual machine scale sets in Azure Security Center",
    "policyType": "BuiltIn",
    "mode": "Indexed",
    "description": "Audit the OS vulnerabilities on your virtual machine scale sets to protect them from attacks.",
    "metadata": {
      "category": "Security Center"
    },
    "parameters": {
      "effect": {
        "type": "String",
        "metadata": {
          "displayName": "Effect",
          "description": "Enable or disable the execution of the policy"
        },
        "allowedValues": [
          "AuditIfNotExists",
          "Disabled"
        ],
        "defaultValue": "AuditIfNotExists"
      }
    },
    "policyRule": {
      "if": {
        "field": "type",
        "equals": "Microsoft.Compute/virtualMachineScaleSets"
      },
      "then": {
        "effect": "[parameters('effect')]",
        "details": {
          "type": "Microsoft.Security/complianceResults",
          "name": "OsVulnerabilities",
          "existenceCondition": {
            "field": "Microsoft.Security/complianceResults/resourceStatus",
            "in": [
              "NotApplicable",
              "OffByPolicy",
              "Healthy"
            ]
          }
        }
      }
    }
  },
  "id": "/providers/Microsoft.Authorization/policyDefinitions/3c735d8a-a4ba-4a3a-b7cf-db7754cf57f4",
  "type": "Microsoft.Authorization/policyDefinitions",
  "name": "3c735d8a-a4ba-4a3a-b7cf-db7754cf57f4"
}

