let GetIPsForHost = (v_Host_HostName:string){
                            Heartbeat
                            | where Computer =~ v_Host_HostName
                            | summarize arg_max(TimeGenerated, *) by ComputerIP
                            };
                            GetIPsForHost('<hostName>')

let GetHostsForIp = (v_IP_Address:string){
                            Heartbeat
                            | where ComputerIP =~ v_IP_Address
                            | summarize arg_max(TimeGenerated, *) by Computer
                            };
                            GetHostsForIp('<address>')

let GetUserPeersWithAlerts = (v_Account_Name:string, v_Account_UPNSuffix:string, v_Account_AadUserId:string) { 
							  let Account_UPN = strcat(v_Account_Name, '@',v_Account_UPNSuffix);
							  let Peers= UserPeerAnalytics  
							      | where UserPrincipalName =~ Account_UPN or UserId =~ v_Account_AadUserId
							      | where TimeGenerated == toscalar (UserPeerAnalytics | summarize max(TimeGenerated))
							      | project PeerUserPrincipalName, PeerUserId, Rank
							      | extend PeerUserPrincipalName=tolower(PeerUserPrincipalName)
							      | parse PeerUserPrincipalName with Account_Name '@' Account_UPNSuffix;
							  let PeerNames= Peers | summarize make_set_if(Account_Name, isnotempty(Account_Name));
							  let PeerIds  = Peers | summarize make_set_if(PeerUserId  , isnotempty(PeerUserId));
							  let PeersWithSecAlert=SecurityAlert
							      | where Entities has "account"
							      | where Entities has_any (PeerNames) or Entities has_any (PeerIds)
							      | mvexpand todynamic(Entities) 
							      | where tostring(parse_json(Entities).Type) =="account" 
							      | where tostring(parse_json(Entities).Name) has_any (PeerNames) or tostring(parse_json(Entities).AadUserId) has_any (PeerIds)
							      | summarize Account_Aux_AlertCount = count() 
							          by Account_Name=tolower(tostring(parse_json(Entities).Name))
							           , Account_UPNSuffix=tolower(tostring(parse_json(Entities).UPNSuffix));
							  PeersWithSecAlert 
							  | join kind=innerunique
							  Peers 
							      on Account_Name, Account_UPNSuffix
							  | project Account_Name, Account_UPNSuffix, Account_Aux_AlertCount
							  };
							  GetUserPeersWithAlerts("{{Account_Name}}","{{Account_UPNSuffix}}", "{{Account_AadUserId}}")

let HostScreenshot= (Account_Name:string, Account_UPNSuffix:string){
							  let p_Account_UPN = strcat(Account_Name,'@',Account_UPNSuffix);
							  DeviceEvents 
							  | where ActionType =='ScreenshotTaken' 
							  | where InitiatingProcessAccountUpn =~ p_Account_UPN
							  | summarize Count=count() by DeviceName
							  | top 10 by Count desc
							  | project Host_UnstructuredName=DeviceName
							};
							HostScreenshot('<Account_Name>', '<Account_UPNSuffix>')

let GetFilesHost = (v_File_Name:string){
							SecurityEvent
							| where CommandLine contains v_File_Name or ServiceFileName contains v_File_Name or ServiceName contains v_File_Name
							| summarize min(TimeGenerated), max(TimeGenerated) by Computer, Host_OMSAgentID=SourceComputerId, Host_AzureID = _ResourceId
							| project min_TimeGenerated, max_TimeGenerated, Computer, Host_OMSAgentID, Host_AzureID
							| project-rename Host_UnstructuredName=Computer, Host_Aux_min_TimeGenerated=min_TimeGenerated, Host_Aux_max_TimeGenerated=max_TimeGenerated
							| top 10 by Host_Aux_min_TimeGenerated desc nulls last
							};
							GetFilesHost('<fileName>')

let GetSysLogEventsOnHost = (v_Host_HostName:string){
							Syslog
							| where Computer has v_Host_HostName
							| extend info = pack('HostName', HostName, 'HostIP', HostIP)
							| summarize Process_Aux_StartTime=min(EventTime), Process_Aux_EndTime=max(EventTime), count(), Process_Aux_info = makeset(info) by Computer, ProcessName, ProcessID
							| top 10 by count_ asc nulls last 
							| project Process_Aux_StartTime, Process_Aux_EndTime, Process_Host_UnstructuredName=Computer, Process_ProcessId=tostring(ProcessID), Process_ImageFile_FullPath=ProcessName, Process_Aux_info
							};
							GetSysLogEventsOnHost('<hostName>')

let BlockedUnsigned = (v_Host_HostName:string){
							DeviceEvents
							| where ActionType == "ExploitGuardNonMicrosoftSignedBlocked" and FileName !hassuffix ".ni.dll"
							| where v_Host_HostName =~ tostring(split(DeviceName, '.')[0])
							| summarize Process_Aux_Count=count() by Process_ProcessId=InitiatingProcessId, Process_CommandLine=InitiatingProcessCommandLine, Process_Host_UnstructuredName=DeviceName
							| top 10 by Process_Aux_Count desc
							};
							BlockedUnsigned('<hostName>')

let AppControlEvents=(v_Host_HostName:string, v_Host_NTDomain:string, v_Host_DnsDomain:string){
							let p_FullDeviceName = iff(isnotempty(v_Host_DnsDomain), strcat(v_Host_HostName,'.',v_Host_DnsDomain), strcat(v_Host_HostName,'.',v_Host_NTDomain));
							let AppControls=datatable(ActionType:string, Description:string, FriendlyActivityName:string)
							 ["AppControlAppInstallationAudited", "Application control detected the installation of an untrusted app.","Untrusted app installed"
							 ,"AppControlAppInstallationBlocked", "Application control blocked the installation of an untrusted app.",  "Untrusted app installation blocked"
							 ,"AppControlCodeIntegrityDriverRevoked", "Application control found a driver with a revoked certificate.", "Driver with revoked certificate detected"
							 ,"AppControlCodeIntegrityImageRevoked", "Application control found an executable file with a revoked certificate.", "Executable with revoked certificate detected"
							 ,"AppControlExecutableAudited","Application control detected the use of an untrusted executable.","Untrusted executable used"
							 ,"AppControlExecutableClocked","Application control blocked the use of an untrusted executable.","Untrusted executable blocked"
							 ,"AppControlScriptAudited", "Application control detected the use of an untrusted script.", "Untrusted script detected"
							 ,"AppControlScriptBlocked", "Application control blocked the use of an untrusted script.", "Untrusted script blocked" ];
							DeviceEvents
							| where ActionType in (AppControls)  
							| where DeviceName ==p_FullDeviceName
							| parse InitiatingProcessAccountUpn with Account_Name '@' Account_UPNSuffix
							| project Account_Name, Account_UPNSuffix, Account_Sid=InitiatingProcessAccountSid
							| summarize Account_Aux_AppConCount=count() by Account_Name, Account_UPNSuffix, Account_Sid
							| top 10 by Account_Aux_AppConCount desc nulls last
							};
							AppControlEvents('<hostName>','<ntDomain>','<dnsDomain>')

