This sample shows how to call StartTranslationAsync.

Uri endpoint = new Uri("<https://my-service.azure.com>");
AzureKeyCredential credential = new AzureKeyCredential("<key>");
DocumentTranslation client = new DocumentTranslationRestClient(endpoint, credential).GetDocumentTranslationClient(apiVersion: "v1.0");

using RequestContent content = RequestContent.Create(new
{
    inputs = new object[]
    {
        new
        {
            source = new
            {
                sourceUrl = "<sourceUrl>",
            },
            targets = new object[]
            {
                new
                {
                    targetUrl = "<targetUrl>",
                    language = "<language>",
                }
            },
        }
    },
});
Response response = await client.StartTranslationAsync(content);

Console.WriteLine(response.Status);

