                            Assert.True(m.Identifier.Name.StartsWith("listkeytest"));
                            addedObjects.Remove(m.Kid);
                        }
                    }
                    nextLink = listNextResponse.NextPageLink;
                }

                Assert.True(addedObjects.Count == 0);
            }
        }

