{
  "value": [
    {
      "properties": {
        "displayName": "Enforce tag exists",
        "policyType": "BuiltIn",
        "description": "Enforces a required tag.",
        "parameters": {
          "tagName": {
            "type": "String",
            "metadata": {
              "description": "Name of the tag, such as costCenter",
              "strongType": "tagName"
            }
          }
        },
        "policyRule": {
          "if": {
            "not": {
              "field": "tags",
              "containsKey": "[parameters('tagName')]"
            }
          },
          "then": {
            "effect": "deny"
          }
        }
      },
      "id": "/providers/Microsoft.Authorization/policyDefinitions/1e30110a-5ceb-460c-a204-c1c3969c6d62",
      "type": "Microsoft.Authorization/policyDefinitions",
      "name": "1e30110a-5ceb-460c-a204-c1c3969c6d62"
    },
    {
      "properties": {
        "displayName": "Apply tag and its default value",
        "policyType": "BuiltIn",
        "description": "Applies a required tag and its default value if it is not specified by the user.",
        "parameters": {
          "tagName": {
            "type": "String",
            "metadata": {
              "description": "Name of the tag, such as costCenter",
              "strongType": "tagName"
            }
          },
          "tagValue": {
            "type": "String",
            "metadata": {
              "description": "Value of the tag, such as headquarter",
              "strongType": "tagValue"
            }
          }
        },
        "policyRule": {
          "if": {
            "field": "[concat('tags.',parameters('tagName'))]",
            "exists": "false"
          },
          "then": {
            "effect": "append",
            "details": [
              {
                "field": "[concat('tags.',parameters('tagName'))]",
                "value": "[parameters('tagValue')]"
              }
            ]
          }
        }
      },
      "id": "/providers/Microsoft.Authorization/policyDefinitions/2a0e14a6-b0a6-4fab-991a-187a4f81c498",
      "type": "Microsoft.Authorization/policyDefinitions",
