    {
      "properties": {
        "displayName": "Azure Resource Manager Restricted Service Catalog",
        "policyType": "BuiltIn",
        "description": "Policy Role blocking access to specified services",
        "policyRule": {
          "if": {
            "anyOf": [
              {
                "source": "action",
                "like": "Microsoft.ClassicCompute/*"
              },
              {
                "source": "action",
                "like": "Microsoft.ClassicStorage/*"
              },
              {
                "source": "action",
                "like": "Microsoft.ClassicNetwork/*"
              }
            ]
          },
          "then": {
            "effect": "Deny"
          }
        }
      },
