      "properties": {
        "displayName": "Virtual Machine SKU policy",
        "policyType": "BuiltIn",
        "description": "This template enables you to specify a set of virtual machine SKUs that your organization can deploy.",
        "parameters": {
          "listOfAllowedSKUs": {
            "type": "Array",
            "metadata": {
              "description": "An array of permitted SKUs for virtual machines.",
              "displayName": "Permitted SKUs",
              "strongType": "VMSKUs"
            }
          }
        },
        "policyRule": {
          "if": {
            "allOf": [
              {
                "field": "type",
                "equals": "Microsoft.Compute/virtualMachines"
              },
              {
                "not": {
                  "allOf": [
                    {
                      "field": "Microsoft.Compute/virtualMachines/sku.name",
                      "in": "[parameters('listOfAllowedSKUs')]"
                    }
                  ]
                }
              }
            ]
          },
          "then": {
            "effect": "deny"
          }
        }
