              "in": "[parameters('allowedLocations')]"
            }
          },
          "then": {
            "effect": "audit"
          }
        }
      },
      "id": "/subscriptions/0c82cadf-f711-4825-bcaf-44189e8baa9f/providers/Microsoft.Authorization/policyDefinitions/carolynPolicyDefinition",
      "type": "Microsoft.Authorization/policyDefinitions",
      "name": "carolynPolicyDefinition"
    },
    {
      "properties": {
        "displayName": "Append tag if not exist",
        "policyType": "Custom",
        "description": "This policy appends a tag to all resources.",
        "policyRule": {
          "if": {
            "not": {
              "field": "tags",
              "containsKey": "cheggTag"
            }
          },
          "then": {
            "effect": "append",
            "details": [
              {
                "field": "tags.cheggTag",
                "value": "defaultValue"
              }
            ]
          }
        }
      },
      "id": "/subscriptions/0c82cadf-f711-4825-bcaf-44189e8baa9f/providers/Microsoft.Authorization/policyDefinitions/cheggTagDefault",
      "type": "Microsoft.Authorization/policyDefinitions",
      "name": "cheggTagDefault"
    },
    {
      "properties": {
        "displayName": "Ensure specific tag exists",
        "policyType": "Custom",
