              "field": "tags",
              "containsKey": "[parameters('tagKey')]"
            }
          },
          "then": {
            "effect": "deny"
          }
        }
      },
      "id": "/subscriptions/0c82cadf-f711-4825-bcaf-44189e8baa9f/providers/Microsoft.Authorization/policyDefinitions/cheggTagExists",
      "type": "Microsoft.Authorization/policyDefinitions",
      "name": "cheggTagExists"
    },
    {
      "properties": {
        "displayName": "Restrict to 4 types (cheggert)",
        "policyType": "Custom",
        "description": "This policy restricts allowed resourcetypes to the big 4.",
        "policyRule": {
          "if": {
            "not": {
              "anyOf": [
                {
                  "field": "type",
                  "like": "Microsoft.Resources/*"
                },
                {
                  "field": "type",
                  "like": "Microsoft.Compute/*"
                },
                {
                  "field": "type",
                  "like": "Microsoft.Storage/*"
                },
                {
                  "field": "type",
                  "like": "Microsoft.Network/*"
                }
              ]
            }
          },
          "then": {
            "effect": "deny"
          }
        }
      },
      "id": "/subscriptions/0c82cadf-f711-4825-bcaf-44189e8baa9f/providers/Microsoft.Authorization/policyDefinitions/cheggtype",
      "type": "Microsoft.Authorization/policyDefinitions",
      "name": "cheggtype"
    },
    {
      "properties": {
        "displayName": "Azure Resource Manager US Only Policy",
        "policyType": "Custom",
        "description": "Policy Role auditing non westus resources",
        "policyRule": {
          "if": {
            "not": {
              "field": "location",
              "in": [
                "westus"
              ]
            }
          },
          "then": {
            "effect": "Audit"
          }
        }
      },
