      "name": "LocationAudit"
    },
    {
      "properties": {
        "displayName": "Audit if network watcher does not exist",
        "policyType": "Custom",
        "description": "This policy audits if a network watcher resource does not exist in the same resource group as new VNets.",
        "policyRule": {
          "if": {
            "field": "type",
            "equals": "Microsoft.KeyVault/vaults"
          },
          "then": {
            "effect": "auditIfNotExists",
            "details": {
              "type": "Microsoft.Network/networkWatchers"
            }
          }
        }
      },
