      "name": "regionPolicyDefinition"
    },
    {
      "properties": {
        "displayName": "Enforce transparentDataEncryption enabled",
        "policyType": "Custom",
        "mode": "All",
        "description": "This policy denies creation/updates of network interface cards if a specific NSG is not included.",
        "policyRule": {
          "if": {
            "allOf": [
              {
                "field": "type",
                "equals": "Microsoft.Sql/servers/databases/transparentDataEncryption"
              },
              {
                "field": "Microsoft.Sql/transparentDataEncryption.status",
                "notequals": "Enabled"
              }
            ]
          },
          "then": {
            "effect": "deny"
          }
        }
      },
