    {
      "properties": {
        "displayName": "[Preview]: Monitor OS vulnerabilities in Security Center",
        "policyType": "BuiltIn",
        "mode": "All",
        "description": "Servers which do not satisfy the configured baseline will be monitored by Azure Security Center as recommendations.",
        "metadata": {
          "category": "Security Center",
          "preview": true
        },
        "parameters": {
          "effect": {
            "type": "String",
            "metadata": {
              "displayName": "Effect",
              "description": "Enable or disable the execution of the policy"
            },
            "allowedValues": [
              "AuditIfNotExists",
              "Disabled"
            ],
            "defaultValue": "AuditIfNotExists"
          }
        },
        "policyRule": {
          "if": {
            "field": "type",
            "in": [
              "Microsoft.Compute/virtualMachines",
              "Microsoft.ClassicCompute/virtualMachines",
              "Microsoft.OperationalInsights/workspaces"
            ]
          },
          "then": {
            "effect": "[parameters('effect')]",
            "details": {
              "type": "Microsoft.Security/complianceResults",
              "name": "osVulnerabilities",
              "existenceCondition": {
                "field": "Microsoft.Security/complianceResults/resourceStatus",
                "equals": "Monitored"
              }
            }
          }
        }
      },
      "id": "/providers/Microsoft.Authorization/policyDefinitions/e1e5fd5d-3e4c-4ce1-8661-7d1873ae6b15",
      "type": "Microsoft.Authorization/policyDefinitions",
      "name": "e1e5fd5d-3e4c-4ce1-8661-7d1873ae6b15"
    },
    {
      "properties": {
        "displayName": "Allowed locations",
        "policyType": "BuiltIn",
        "description": "This policy enables you to restrict the locations your organization can specify when deploying resources. Use to enforce your geo-compliance requirements. Excludes resource groups, Microsoft.AzureActiveDirectory/b2cDirectories, and resources that use the 'global' region.",
        "metadata": {
          "category": "General"
        },
        "parameters": {
          "listOfAllowedLocations": {
            "type": "Array",
            "metadata": {
              "description": "The list of locations that can be specified when deploying resources.",
              "strongType": "location",
              "displayName": "Allowed locations"
            }
          }
        },
        "policyRule": {
          "if": {
            "allOf": [
              {
                "field": "location",
                "notIn": "[parameters('listOfAllowedLocations')]"
              },
              {
                "field": "location",
                "notEquals": "global"
              },
              {
                "field": "type",
                "notEquals": "Microsoft.AzureActiveDirectory/b2cDirectories"
              }
            ]
          },
          "then": {
            "effect": "deny"
          }
        }
      },
      "id": "/providers/Microsoft.Authorization/policyDefinitions/e56962a6-4747-49cd-b67b-bf8b01975c4c",
      "type": "Microsoft.Authorization/policyDefinitions",
      "name": "e56962a6-4747-49cd-b67b-bf8b01975c4c"
    },
    {
      "properties": {
        "displayName": "Allowed locations for resource groups",
        "policyType": "BuiltIn",
        "mode": "All",
        "description": "This policy enables you to restrict the locations your organization can create resource groups in. Use to enforce your geo-compliance requirements.",
        "metadata": {
          "category": "General"
        },
        "parameters": {
          "listOfAllowedLocations": {
            "type": "Array",
            "metadata": {
              "description": "The list of locations that resource groups can be created in.",
              "strongType": "location",
              "displayName": "Allowed locations"
            }
          }
        },
        "policyRule": {
          "if": {
