      },
      "id": "/providers/Microsoft.Authorization/policyDefinitions/cd8dc879-a2ae-43c3-8211-1877c5755064",
      "type": "Microsoft.Authorization/policyDefinitions",
      "name": "cd8dc879-a2ae-43c3-8211-1877c5755064"
    },
    {
