{
  "value": [
    {
      "properties": {
        "displayName": "Audit enabling of diagnostic logs in Azure Data Lake Store",
        "policyType": "BuiltIn",
        "mode": "Indexed",
        "description": "Audit enabling of logs and retain them up to a year. This enables you to recreate activity trails for investigation purposes when a security incident occurs or your network is compromised",
        "metadata": {
          "category": "Data Lake"
        },
        "parameters": {
          "effect": {
            "type": "String",
            "metadata": {
              "displayName": "Effect",
              "description": "Enable or disable the execution of the policy"
            },
            "allowedValues": [
              "AuditIfNotExists",
              "Disabled"
            ],
            "defaultValue": "AuditIfNotExists"
          },
          "requiredRetentionDays": {
            "type": "String",
            "metadata": {
              "displayName": "Required retention (days)",
              "description": "The required diagnostic logs retention in days"
            },
            "defaultValue": "365"
          }
        },
        "policyRule": {
          "if": {
            "field": "type",
            "equals": "Microsoft.DataLakeStore/accounts"
          },
          "then": {
            "effect": "[parameters('effect')]",
            "details": {
              "type": "Microsoft.Insights/diagnosticSettings",
              "existenceCondition": {
                "allOf": [
                  {
                    "field": "Microsoft.Insights/diagnosticSettings/logs[*].retentionPolicy.enabled",
                    "equals": "true"
                  },
                  {
                    "field": "Microsoft.Insights/diagnosticSettings/logs[*].retentionPolicy.days",
                    "equals": "[parameters('requiredRetentionDays')]"
                  }
                ]
              }
            }
          }
        }
      },
      "id": "/providers/Microsoft.Authorization/policyDefinitions/057ef27e-665e-4328-8ea3-04b3122bd9fb",
      "type": "Microsoft.Authorization/policyDefinitions",
      "name": "057ef27e-665e-4328-8ea3-04b3122bd9fb"
    },
    {
      "properties": {
        "displayName": "Audit SQL DB Level Audit Setting",
        "policyType": "BuiltIn",
        "mode": "All",
        "description": "Audit DB level audit setting for SQL databases",
        "metadata": {
          "category": "SQL",
          "deprecated": true
        },
        "parameters": {
          "setting": {
            "type": "String",
            "metadata": {
              "displayName": "Audit Setting"
            },
            "allowedValues": [
              "enabled",
              "disabled"
            ]
          }
        },
        "policyRule": {
          "if": {
            "field": "type",
            "equals": "Microsoft.Sql/servers/databases"
          },
          "then": {
            "effect": "AuditIfNotExists",
            "details": {
              "type": "Microsoft.Sql/servers/databases/auditingSettings",
              "name": "default",
              "existenceCondition": {
                "allOf": [
                  {
                    "field": "Microsoft.Sql/auditingSettings.state",
                    "equals": "[parameters('setting')]"
                  }
                ]
              }
            }
          }
        }
      },
      "id": "/providers/Microsoft.Authorization/policyDefinitions/06a78e20-9358-41c9-923c-fb736d382a12",
      "type": "Microsoft.Authorization/policyDefinitions",
      "name": "06a78e20-9358-41c9-923c-fb736d382a12"
    },
    {
      "properties": {
        "displayName": "Audit VMs that do not use managed disks",
        "policyType": "BuiltIn",
        "mode": "All",
        "description": "This policy audits VMs that do not use managed disks",
        "metadata": {
          "category": "Compute"
        },
        "parameters": {},
        "policyRule": {
          "if": {
            "anyOf": [
              {
                "allOf": [
                  {
                    "field": "type",
                    "equals": "Microsoft.Compute/virtualMachines"
                  },
                  {
                    "field": "Microsoft.Compute/virtualMachines/osDisk.uri",
                    "exists": "True"
                  }
                ]
              },
              {
                "allOf": [
                  {
                    "field": "type",
                    "equals": "Microsoft.Compute/VirtualMachineScaleSets"
                  },
                  {
                    "anyOf": [
                      {
                        "field": "Microsoft.Compute/VirtualMachineScaleSets/osDisk.vhdContainers",
                        "exists": "True"
                      },
                      {
                        "field": "Microsoft.Compute/VirtualMachineScaleSets/osdisk.imageUrl",
                        "exists": "True"
                      }
                    ]
                  }
                ]
              }
            ]
          },
          "then": {
            "effect": "audit"
          }
        }
      },
      "id": "/providers/Microsoft.Authorization/policyDefinitions/06a78e20-9358-41c9-923c-fb736d382a4d",
      "type": "Microsoft.Authorization/policyDefinitions",
      "name": "06a78e20-9358-41c9-923c-fb736d382a4d"
    },
    {
      "properties": {
        "displayName": "[Preview]: Deploy default OMS VM Extension for Windows VMs.",
        "policyType": "BuiltIn",
        "mode": "NotSpecified",
        "description": "This policy deploys OMS VM Extensions on Windows VMs, and connects to the selected Log Analytics workspace",
        "metadata": {
          "category": "Compute"
        },
        "parameters": {
          "logAnalytics": {
            "type": "String",
            "metadata": {
              "displayName": "Log Analytics workspace",
              "description": "Select Log Analytics workspace from dropdown list",
              "strongType": "omsWorkspace"
            }
          }
        },
        "policyRule": {
          "if": {
            "allOf": [
              {
                "field": "type",
                "equals": "Microsoft.Compute/virtualMachines"
              },
              {
                "field": "Microsoft.Compute/imagePublisher",
                "equals": "MicrosoftWindowsServer"
              },
              {
                "field": "Microsoft.Compute/imageOffer",
                "equals": "WindowsServer"
              },
              {
                "field": "Microsoft.Compute/imageSKU",
                "in": [
                  "2008-R2-SP1",
                  "2008-R2-SP1-smalldisk",
                  "2012-Datacenter",
                  "2012-Datacenter-smalldisk",
                  "2012-R2-Datacenter",
                  "2012-R2-Datacenter-smalldisk",
                  "2016-Datacenter",
                  "2016-Datacenter-Server-Core",
                  "2016-Datacenter-Server-Core-smalldisk",
                  "2016-Datacenter-smalldisk",
                  "2016-Datacenter-with-Containers",
                  "2016-Datacenter-with-RDSH"
                ]
              }
            ]
          },
          "then": {
            "effect": "deployIfNotExists",
            "details": {
              "type": "Microsoft.Compute/virtualMachines/extensions",
              "existenceCondition": {
                "allOf": [
                  {
                    "field": "Microsoft.Compute/virtualMachines/extensions/type",
                    "equals": "MicrosoftMonitoringAgent"
                  },
                  {
                    "field": "Microsoft.Compute/virtualMachines/extensions/publisher",
                    "equals": "Microsoft.EnterpriseCloud.Monitoring"
                  }
                ]
              },
              "deployment": {
                "properties": {
                  "mode": "incremental",
                  "template": {
                    "$schema": "http://schema.management.azure.com/schemas/2015-01-01/deploymentTemplate.json#",
                    "contentVersion": "1.0.0.0",
                    "parameters": {
                      "vmName": {
                        "type": "string"
                      },
                      "location": {
                        "type": "string"
                      },
                      "logAnalytics": {
                        "type": "string"
                      }
                    },
                    "resources": [
                      {
                        "name": "[concat(parameters('vmName'),'/omsPolicy')]",
                        "type": "Microsoft.Compute/virtualMachines/extensions",
                        "location": "[parameters('location')]",
                        "apiVersion": "2017-12-01",
                        "properties": {
                          "publisher": "Microsoft.EnterpriseCloud.Monitoring",
                          "type": "MicrosoftMonitoringAgent",
                          "typeHandlerVersion": "1.0",
                          "autoUpgradeMinorVersion": true,
                          "settings": {
                            "workspaceId": "[reference(parameters('logAnalytics'), '2015-03-20').customerId]"
                          },
                          "protectedSettings": {
                            "workspaceKey": "[listKeys(parameters('logAnalytics'), '2015-03-20').primarySharedKey]"
                          }
                        }
                      }
                    ],
                    "outputs": {
                      "policy": {
                        "type": "string",
                        "value": "[concat('Enabled monitoring for Windows VM', ': ', parameters('vmName'))]"
                      }
                    }
                  },
                  "parameters": {
                    "vmName": {
                      "value": "[field('name')]"
                    },
                    "location": {
                      "value": "[field('location')]"
                    },
                    "logAnalytics": {
                      "value": "[parameters('logAnalytics')]"
                    }
                  }
                }
              }
            }
          }
        }
      },
      "id": "/providers/Microsoft.Authorization/policyDefinitions/0868462e-646c-4fe3-9ced-a733534b6a2c",
      "type": "Microsoft.Authorization/policyDefinitions",
      "name": "0868462e-646c-4fe3-9ced-a733534b6a2c"
    },
    {
      "properties": {
        "displayName": "[Preview]: Monitor unencrypted VM Disks in Azure Security Center",
        "policyType": "BuiltIn",
        "mode": "All",
        "description": "VMs without an enabled disk encryption will be monitored by Azure Security Center as recommendations",
        "metadata": {
          "category": "Security Center",
          "preview": true
        },
        "parameters": {
          "effect": {
            "type": "String",
            "metadata": {
              "displayName": "Effect",
              "description": "Enable or disable the execution of the policy"
            },
            "allowedValues": [
              "AuditIfNotExists",
              "Disabled"
            ],
            "defaultValue": "AuditIfNotExists"
          }
        },
        "policyRule": {
          "if": {
            "field": "type",
            "equals": "Microsoft.Compute/virtualMachines"
          },
          "then": {
            "effect": "[parameters('effect')]",
            "details": {
              "type": "Microsoft.Security/complianceResults",
              "name": "encryption",
              "existenceCondition": {
                "field": "Microsoft.Security/complianceResults/resourceStatus",
                "equals": "Monitored"
              }
            }
          }
        }
      },
      "id": "/providers/Microsoft.Authorization/policyDefinitions/0961003e-5a0a-4549-abde-af6a37f2724d",
      "type": "Microsoft.Authorization/policyDefinitions",
      "name": "0961003e-5a0a-4549-abde-af6a37f2724d"
    },
    {
      "properties": {
        "displayName": "Audit resource location matches resource group location",
        "policyType": "BuiltIn",
        "mode": "Indexed",
        "description": "Audit that the resource location matches its resource group location",
        "metadata": {
          "category": "General"
        },
        "policyRule": {
          "if": {
            "field": "location",
            "notIn": [
              "[resourcegroup().location]",
              "global"
            ]
          },
          "then": {
            "effect": "audit"
          }
        }
      },
      "id": "/providers/Microsoft.Authorization/policyDefinitions/0a914e76-4921-4c19-b460-a2d36003525a",
      "type": "Microsoft.Authorization/policyDefinitions",
      "name": "0a914e76-4921-4c19-b460-a2d36003525a"
    },
    {
      "properties": {
        "displayName": "Audit transparent data encryption status",
        "policyType": "BuiltIn",
        "mode": "Indexed",
        "description": "Audit transparent data encryption status for SQL databases",
        "metadata": {
          "category": "SQL"
        },
        "parameters": {},
        "policyRule": {
          "if": {
            "field": "type",
            "equals": "Microsoft.Sql/servers/databases"
          },
          "then": {
            "effect": "AuditIfNotExists",
            "details": {
              "type": "Microsoft.Sql/servers/databases/transparentDataEncryption",
              "name": "current",
              "existenceCondition": {
                "allOf": [
                  {
                    "field": "Microsoft.Sql/transparentDataEncryption.status",
                    "equals": "enabled"
                  }
                ]
              }
            }
          }
        }
      },
      "id": "/providers/Microsoft.Authorization/policyDefinitions/17k78e20-9358-41c9-923c-fb736d382a12",
      "type": "Microsoft.Authorization/policyDefinitions",
      "name": "17k78e20-9358-41c9-923c-fb736d382a12"
    },
    {
      "properties": {
        "displayName": "Audit use of classic virtual machines",
        "policyType": "BuiltIn",
        "mode": "All",
        "description": "Use new Azure Resource Manager v2 for your virtual machines to provide security enhancements such as: stronger access control (RBAC), better auditing, ARM-based deployment and governance, access to managed identities, access to key vault for secrets, Azure AD-based authentication and support for tags and resource groups for easier security management",
        "metadata": {
          "category": "Compute"
        },
        "parameters": {
          "effect": {
            "type": "String",
            "metadata": {
              "displayName": "Effect",
              "description": "Enable or disable the execution of the policy"
            },
            "allowedValues": [
              "Audit",
              "Disabled"
            ],
            "defaultValue": "Audit"
          }
        },
        "policyRule": {
          "if": {
            "field": "type",
            "equals": "Microsoft.classicCompute/virtualMachines"
          },
          "then": {
            "effect": "[parameters('effect')]"
          }
        }
      },
      "id": "/providers/Microsoft.Authorization/policyDefinitions/1d84d5fb-01f6-4d12-ba4f-4a26081d403d",
      "type": "Microsoft.Authorization/policyDefinitions",
      "name": "1d84d5fb-01f6-4d12-ba4f-4a26081d403d"
    },
    {
      "properties": {
        "displayName": "Enforce tag and its value",
        "policyType": "BuiltIn",
        "description": "Enforces a required tag and its value. Does not apply to resource groups.",
        "metadata": {
          "category": "General"
        },
        "parameters": {
          "tagName": {
            "type": "String",
            "metadata": {
              "displayName": "Tag Name",
              "description": "Name of the tag, such as 'environment'"
            }
          },
          "tagValue": {
            "type": "String",
            "metadata": {
              "displayName": "Tag Value",
              "description": "Value of the tag, such as 'production'"
            }
          }
        },
        "policyRule": {
          "if": {
            "not": {
              "field": "[concat('tags[', parameters('tagName'), ']')]",
              "equals": "[parameters('tagValue')]"
            }
          },
          "then": {
            "effect": "deny"
          }
        }
      },
      "id": "/providers/Microsoft.Authorization/policyDefinitions/1e30110a-5ceb-460c-a204-c1c3969c6d62",
      "type": "Microsoft.Authorization/policyDefinitions",
      "name": "1e30110a-5ceb-460c-a204-c1c3969c6d62"
    },
    {
      "properties": {
        "displayName": "Audit provisioning of an Azure Active Directory administrator for SQL server",
        "policyType": "BuiltIn",
        "mode": "Indexed",
        "description": "Audit provisioning of an Azure Active Directory administrator for your SQL server to enable Azure AD authentication. Azure AD authentication enables simplified permission management and centralized identity management of database users and other Microsoft services",
        "metadata": {
          "category": "SQL"
        },
        "parameters": {
          "effect": {
            "type": "String",
            "metadata": {
              "displayName": "Effect",
              "description": "Enable or disable the execution of the policy"
            },
            "allowedValues": [
              "AuditIfNotExists",
              "Disabled"
            ],
            "defaultValue": "AuditIfNotExists"
          }
        },
        "policyRule": {
          "if": {
            "field": "type",
            "equals": "Microsoft.Sql/servers"
          },
          "then": {
            "effect": "[parameters('effect')]",
            "details": {
              "type": "Microsoft.Sql/servers/administrators"
            }
          }
        }
      },
      "id": "/providers/Microsoft.Authorization/policyDefinitions/1f314764-cb73-4fc9-b863-8eca98ac36e9",
      "type": "Microsoft.Authorization/policyDefinitions",
      "name": "1f314764-cb73-4fc9-b863-8eca98ac36e9"
    },
    {
      "properties": {
        "displayName": "[Preview]: Monitor unprotected web application in Azure Security Center",
        "policyType": "BuiltIn",
        "mode": "All",
        "description": "Web applications without a Web Application Firewall protection will be monitored by Azure Security Center as recommendations",
        "metadata": {
          "category": "Security Center",
          "preview": true
        },
        "parameters": {
          "effect": {
            "type": "String",
            "metadata": {
              "displayName": "Effect",
              "description": "Enable or disable the execution of the policy"
            },
            "allowedValues": [
              "AuditIfNotExists",
              "Disabled"
            ],
            "defaultValue": "AuditIfNotExists"
          }
        },
        "policyRule": {
          "if": {
            "field": "type",
            "in": [
              "Microsoft.Network/publicIPAddresses",
              "Microsoft.ClassicCompute/domainNames",
              "Microsoft.Web/hostingEnvironments"
            ]
          },
          "then": {
            "effect": "[parameters('effect')]",
            "details": {
              "type": "Microsoft.Security/complianceResults",
              "name": "unprotectedWebApplication",
              "existenceCondition": {
                "field": "Microsoft.Security/complianceResults/resourceStatus",
                "equals": "Monitored"
              }
            }
          }
        }
      },
      "id": "/providers/Microsoft.Authorization/policyDefinitions/201ea587-7c90-41c3-910f-c280ae01cfd6",
      "type": "Microsoft.Authorization/policyDefinitions",
      "name": "201ea587-7c90-41c3-910f-c280ae01cfd6"
    },
    {
      "properties": {
        "displayName": "Audit enabling of only secure connections to your Redis Cache",
        "policyType": "BuiltIn",
        "mode": "All",
        "description": "Audit enabling of only connections via SSL to Redis Cache. Use of secure connections ensures authentication between the server and the service and protects data in transit from network layer attacks such as man-in-the-middle, eavesdropping, and session-hijacking",
        "metadata": {
          "category": "Cache"
        },
        "parameters": {
          "effect": {
            "type": "String",
            "metadata": {
              "displayName": "Effect",
              "description": "Enable or disable the execution of the policy"
            },
            "allowedValues": [
              "Audit",
              "Disabled"
            ],
            "defaultValue": "Audit"
          }
        },
        "policyRule": {
          "if": {
            "allOf": [
              {
                "field": "type",
                "equals": "Microsoft.Cache/redis"
              },
              {
                "field": "Microsoft.Cache/Redis/enableNonSslPort",
                "equals": "true"
              }
            ]
          },
          "then": {
            "effect": "[parameters('effect')]"
          }
        }
      },
      "id": "/providers/Microsoft.Authorization/policyDefinitions/22bee202-a82f-4305-9a2a-6d7f44d4dedb",
      "type": "Microsoft.Authorization/policyDefinitions",
      "name": "22bee202-a82f-4305-9a2a-6d7f44d4dedb"
    },
    {
      "properties": {
        "displayName": "Audit configuration of metric alert rules on Batch accounts",
        "policyType": "BuiltIn",
        "mode": "Indexed",
        "description": "Audit configuration of metric alert rules on Batch account to enable the required metric",
        "metadata": {
          "category": "Batch"
        },
        "parameters": {
          "effect": {
            "type": "String",
            "metadata": {
              "displayName": "Effect",
              "description": "Enable or disable the execution of the policy"
            },
            "allowedValues": [
              "AuditIfNotExists",
              "Disabled"
            ],
            "defaultValue": "AuditIfNotExists"
          },
          "metricName": {
            "type": "String",
            "metadata": {
              "displayName": "Metric name",
              "description": "The metric name that an alert rule must be enabled on"
            }
          }
        },
        "policyRule": {
          "if": {
            "field": "type",
            "equals": "Microsoft.Batch/batchAccounts"
          },
          "then": {
            "effect": "[parameters('effect')]",
            "details": {
              "type": "Microsoft.Insights/alertRules",
              "existenceScope": "Subscription",
              "existenceCondition": {
                "allOf": [
                  {
                    "field": "Microsoft.Insights/alertRules/isEnabled",
                    "equals": "true"
                  },
                  {
                    "field": "Microsoft.Insights/alertRules/condition.dataSource.metricName",
                    "equals": "[parameters('metricName')]"
                  },
                  {
                    "field": "Microsoft.Insights/alertRules/condition.dataSource.resourceUri",
                    "equals": "[concat('/subscriptions/', subscription().subscriptionId, '/resourcegroups/', resourceGroup().name, '/providers/Microsoft.Batch/batchAccounts/', field('name'))]"
                  }
                ]
              }
            }
          }
        }
      },
      "id": "/providers/Microsoft.Authorization/policyDefinitions/26ee67a2-f81a-4ba8-b9ce-8550bd5ee1a7",
      "type": "Microsoft.Authorization/policyDefinitions",
      "name": "26ee67a2-f81a-4ba8-b9ce-8550bd5ee1a7"
    },
    {
      "properties": {
        "displayName": "[Preview]: Deploy default Microsoft IaaSAntimalware extension for Windows Server",
        "policyType": "BuiltIn",
        "mode": "Indexed",
        "description": "This policy deploys a Microsoft IaaSAntimalware extension with a default configuraion when a VM is not configured with the antimalware extension.",
        "metadata": {
          "category": "Compute"
        },
        "parameters": {},
        "policyRule": {
          "if": {
            "allOf": [
              {
                "field": "type",
                "equals": "Microsoft.Compute/virtualMachines"
              },
              {
                "field": "Microsoft.Compute/imagePublisher",
                "equals": "MicrosoftWindowsServer"
              },
              {
                "field": "Microsoft.Compute/imageOffer",
                "equals": "WindowsServer"
              },
              {
                "field": "Microsoft.Compute/imageSKU",
                "in": [
                  "2008-R2-SP1",
                  "2008-R2-SP1-smalldisk",
                  "2012-Datacenter",
                  "2012-Datacenter-smalldisk",
                  "2012-R2-Datacenter",
                  "2012-R2-Datacenter-smalldisk",
                  "2016-Datacenter",
                  "2016-Datacenter-Server-Core",
                  "2016-Datacenter-Server-Core-smalldisk",
                  "2016-Datacenter-smalldisk",
                  "2016-Datacenter-with-Containers",
                  "2016-Datacenter-with-RDSH"
                ]
              }
            ]
          },
          "then": {
            "effect": "deployIfNotExists",
            "details": {
              "type": "Microsoft.Compute/virtualMachines/extensions",
              "existenceCondition": {
                "allOf": [
                  {
                    "field": "Microsoft.Compute/virtualMachines/extensions/type",
                    "equals": "IaaSAntimalware"
                  },
                  {
                    "field": "Microsoft.Compute/virtualMachines/extensions/publisher",
                    "equals": "Microsoft.Azure.Security"
                  }
                ]
              },
              "deployment": {
                "properties": {
                  "mode": "incremental",
                  "template": {
                    "$schema": "http://schema.management.azure.com/schemas/2015-01-01/deploymentTemplate.json#",
                    "contentVersion": "1.0.0.0",
                    "parameters": {
                      "vmName": {
                        "type": "string"
                      },
                      "location": {
                        "type": "string"
                      },
                      "ExclusionsPaths": {
                        "type": "string",
                        "defaultValue": "",
                        "metadata": {
                          "description": "Semicolon delimited list of file paths or locations to exclude from scanning"
                        }
                      },
                      "ExclusionsExtensions": {
                        "type": "string",
                        "defaultValue": "",
                        "metadata": {
                          "description": "Semicolon delimited list of file extensions to exclude from scanning"
                        }
                      },
                      "ExclusionsProcesses": {
                        "type": "string",
                        "defaultValue": "",
                        "metadata": {
                          "description": "Semicolon delimited list of process names to exclude from scanning"
                        }
                      },
                      "RealtimeProtectionEnabled": {
                        "type": "string",
                        "defaultValue": "true",
                        "metadata": {
                          "description": "Indicates whether or not real time protection is enabled (default is true)"
                        }
                      },
                      "ScheduledScanSettingsIsEnabled": {
                        "type": "string",
                        "defaultValue": "false",
                        "metadata": {
                          "description": "Indicates whether or not custom scheduled scan settings are enabled (default is false)"
                        }
                      },
                      "ScheduledScanSettingsScanType": {
                        "type": "string",
                        "defaultValue": "Quick",
                        "metadata": {
                          "description": "Indicates whether scheduled scan setting type is set to Quick or Full (default is Quick)"
                        }
                      },
                      "ScheduledScanSettingsDay": {
                        "type": "string",
                        "defaultValue": "7",
                        "metadata": {
                          "description": "Day of the week for scheduled scan (1-Sunday, 2-Monday, ..., 7-Saturday)"
                        }
                      },
                      "ScheduledScanSettingsTime": {
                        "type": "string",
                        "defaultValue": "120",
                        "metadata": {
                          "description": "When to perform the scheduled scan, measured in minutes from midnight (0-1440). For example: 0 = 12AM, 60 = 1AM, 120 = 2AM."
