        "policyType": "BuiltIn",
        "mode": "Indexed",
        "description": "Audit that the resource location matches its resource group location",
        "metadata": {
          "category": "General"
        },
        "policyRule": {
          "if": {
            "field": "location",
            "notIn": [
              "[resourcegroup().location]",
              "global"
            ]
          },
          "then": {
            "effect": "audit"
          }
        }
      },
      "id": "/providers/Microsoft.Authorization/policyDefinitions/0a914e76-4921-4c19-b460-a2d36003525a",
      "type": "Microsoft.Authorization/policyDefinitions",
      "name": "0a914e76-4921-4c19-b460-a2d36003525a"
    },
    {
      "properties": {
        "displayName": "Audit transparent data encryption status",
        "policyType": "BuiltIn",
        "mode": "Indexed",
        "description": "Audit transparent data encryption status for SQL databases",
        "metadata": {
          "category": "SQL"
        },
        "parameters": {},
        "policyRule": {
          "if": {
            "field": "type",
            "equals": "Microsoft.Sql/servers/databases"
          },
          "then": {
            "effect": "AuditIfNotExists",
            "details": {
              "type": "Microsoft.Sql/servers/databases/transparentDataEncryption",
              "name": "current",
              "existenceCondition": {
                "allOf": [
                  {
                    "field": "Microsoft.Sql/transparentDataEncryption.status",
                    "equals": "enabled"
                  }
                ]
              }
            }
          }
        }
      },
      "id": "/providers/Microsoft.Authorization/policyDefinitions/17k78e20-9358-41c9-923c-fb736d382a12",
      "type": "Microsoft.Authorization/policyDefinitions",
      "name": "17k78e20-9358-41c9-923c-fb736d382a12"
    },
    {
      "properties": {
        "displayName": "Audit use of classic virtual machines",
        "policyType": "BuiltIn",
        "mode": "All",
        "description": "Use new Azure Resource Manager v2 for your virtual machines to provide security enhancements such as: stronger access control (RBAC), better auditing, ARM-based deployment and governance, access to managed identities, access to key vault for secrets, Azure AD-based authentication and support for tags and resource groups for easier security management",
        "metadata": {
          "category": "Compute"
        },
        "parameters": {
          "effect": {
            "type": "String",
            "metadata": {
              "displayName": "Effect",
              "description": "Enable or disable the execution of the policy"
            },
            "allowedValues": [
              "Audit",
              "Disabled"
            ],
            "defaultValue": "Audit"
          }
        },
        "policyRule": {
          "if": {
            "field": "type",
            "equals": "Microsoft.classicCompute/virtualMachines"
          },
          "then": {
            "effect": "[parameters('effect')]"
          }
        }
      },
      "id": "/providers/Microsoft.Authorization/policyDefinitions/1d84d5fb-01f6-4d12-ba4f-4a26081d403d",
      "type": "Microsoft.Authorization/policyDefinitions",
      "name": "1d84d5fb-01f6-4d12-ba4f-4a26081d403d"
    },
    {
      "properties": {
        "displayName": "Enforce tag and its value",
        "policyType": "BuiltIn",
        "description": "Enforces a required tag and its value. Does not apply to resource groups.",
        "metadata": {
          "category": "General"
        },
        "parameters": {
          "tagName": {
            "type": "String",
            "metadata": {
              "displayName": "Tag Name",
              "description": "Name of the tag, such as 'environment'"
            }
          },
          "tagValue": {
            "type": "String",
            "metadata": {
              "displayName": "Tag Value",
              "description": "Value of the tag, such as 'production'"
            }
          }
        },
        "policyRule": {
          "if": {
            "not": {
              "field": "[concat('tags[', parameters('tagName'), ']')]",
              "equals": "[parameters('tagValue')]"
            }
          },
          "then": {
            "effect": "deny"
          }
        }
      },
      "id": "/providers/Microsoft.Authorization/policyDefinitions/1e30110a-5ceb-460c-a204-c1c3969c6d62",
