        "policyType": "BuiltIn",
        "mode": "Indexed",
        "description": "This policy automatically deploys diagnostic settings to network security groups.",
        "metadata": {
          "category": "Monitoring"
        },
        "parameters": {
          "storagePrefix": {
            "type": "String",
            "metadata": {
              "displayName": "Storage Account Prefix for Regional Storage Account"
            }
