{
  "properties": {
    "displayName": "Audit authorization rules on Event Hub namespaces",
    "policyType": "BuiltIn",
    "mode": "Indexed",
    "description": "Event Hub clients should not use a namespace level access policy that provides access to all queues and topics in a namespace. To align with the least previlege security model, you shoud create access policies at the entity level for queues and topics to provide access to only the specific entity",
    "metadata": {
      "category": "Event Hub"
    },
    "parameters": {
      "effect": {
        "type": "String",
        "metadata": {
          "displayName": "Effect",
          "description": "Enable or disable the execution of the policy"
        },
        "allowedValues": [
          "Audit",
          "Disabled"
        ],
        "defaultValue": "Audit"
      }
    },
    "policyRule": {
      "if": {
        "allOf": [
          {
            "field": "name",
            "notEquals": "RootManageSharedAccessKey"
          },
          {
            "field": "type",
            "equals": "Microsoft.EventHub/namespaces/authorizationRules"
          }
        ]
      },
      "then": {
        "effect": "[parameters('effect')]"
      }
    }
  },
  "id": "/providers/Microsoft.Authorization/policyDefinitions/b278e460-7cfc-4451-8294-cccc40a940d7",
  "type": "Microsoft.Authorization/policyDefinitions",
  "name": "b278e460-7cfc-4451-8294-cccc40a940d7"
}

