      "type": "Microsoft.Authorization/policyDefinitions",
      "name": "37e0d2fe-28a5-43d6-a273-67d37d1f5606"
    },
    {
      "properties": {
        "displayName": "[Preview]: Deploy default OMS VM Extension for Ubuntu VMs.",
        "policyType": "BuiltIn",
        "mode": "Indexed",
        "description": "This policy deploys OMS VM Extensions on Ubuntu VMs, and connects to the selected Log Analytics workspace",
        "metadata": {
          "category": "Compute"
        },
        "parameters": {
          "logAnalytics": {
            "type": "String",
            "metadata": {
              "displayName": "Log Analytics workspace",
              "description": "Select Log Analytics workspace from dropdown list",
              "strongType": "omsWorkspace"
            }
          }
        },
        "policyRule": {
          "if": {
            "allOf": [
              {
                "field": "type",
                "equals": "Microsoft.Compute/virtualMachines"
              },
              {
                "field": "Microsoft.Compute/imagePublisher",
                "equals": "Canonical"
              },
              {
                "field": "Microsoft.Compute/imageOffer",
                "equals": "UbuntuServer"
              },
              {
                "field": "Microsoft.Compute/imageSKU",
                "in": [
                  "14.04.2-LTS",
                  "12.04.5-LTS"
                ]
              }
            ]
          },
          "then": {
            "effect": "deployIfNotExists",
            "details": {
              "type": "Microsoft.Compute/virtualMachines/extensions",
              "existenceCondition": {
                "allOf": [
                  {
                    "field": "Microsoft.Compute/virtualMachines/extensions/type",
                    "equals": "OmsAgentForLinux"
                  },
                  {
                    "field": "Microsoft.Compute/virtualMachines/extensions/publisher",
                    "equals": "Microsoft.EnterpriseCloud.Monitoring"
                  }
                ]
              },
              "deployment": {
                "properties": {
                  "mode": "incremental",
                  "template": {
                    "$schema": "http://schema.management.azure.com/schemas/2015-01-01/deploymentTemplate.json#",
                    "contentVersion": "1.0.0.0",
                    "parameters": {
                      "vmName": {
                        "type": "string"
                      },
                      "location": {
                        "type": "string"
                      },
                      "logAnalytics": {
                        "type": "string"
                      }
                    },
                    "resources": [
                      {
                        "name": "[concat(parameters('vmName'),'/omsPolicy')]",
                        "type": "Microsoft.Compute/virtualMachines/extensions",
                        "location": "[parameters('location')]",
                        "apiVersion": "2017-12-01",
                        "properties": {
                          "publisher": "Microsoft.EnterpriseCloud.Monitoring",
                          "type": "OmsAgentForLinux",
                          "typeHandlerVersion": "1.4",
                          "autoUpgradeMinorVersion": true,
                          "settings": {
                            "workspaceId": "[reference(parameters('logAnalytics'), '2015-03-20').customerId]"
                          },
                          "protectedSettings": {
                            "workspaceKey": "[listKeys(parameters('logAnalytics'), '2015-03-20').primarySharedKey]"
                          }
                        }
                      }
                    ],
                    "outputs": {
                      "policy": {
                        "type": "string",
                        "value": "[concat('Enabled monitoring for Linux VM', ': ', parameters('vmName'))]"
                      }
                    }
                  },
                  "parameters": {
                    "vmName": {
                      "value": "[field('name')]"
                    },
                    "location": {
                      "value": "[field('location')]"
                    },
                    "logAnalytics": {
                      "value": "[parameters('logAnalytics')]"
                    }
                  }
                }
              }
            }
          }
        }
      },
      "id": "/providers/Microsoft.Authorization/policyDefinitions/3d8640fc-63f6-4734-8dcb-cfd3d8c78f38",
      "type": "Microsoft.Authorization/policyDefinitions",
      "name": "3d8640fc-63f6-4734-8dcb-cfd3d8c78f38"
    },
    {
      "properties": {
        "displayName": "Audit secure transfer to storage accounts",
        "policyType": "BuiltIn",
        "mode": "Indexed",
        "description": "Audit requirment of Secure transfer in your storage account. Secure transfer is an option that forces your storage account to accept requests only from secure connections (HTTPS). Use of HTTPS ensures authentication between the server and the service and protects data in transit from network layer attacks such as man-in-the-middle, eavesdropping, and session-hijacking",
        "metadata": {
          "category": "Storage"
        },
        "parameters": {
          "effect": {
            "type": "String",
            "metadata": {
              "displayName": "Effect",
              "description": "Enable or disable the execution of the policy"
            },
            "allowedValues": [
              "Audit",
              "Disabled"
            ],
            "defaultValue": "Audit"
          }
        },
        "policyRule": {
          "if": {
            "allOf": [
              {
                "field": "type",
                "equals": "Microsoft.Storage/storageAccounts"
              },
              {
                "not": {
                  "field": "Microsoft.Storage/storageAccounts/supportsHttpsTrafficOnly",
                  "equals": "True"
                }
              }
            ]
          },
          "then": {
            "effect": "[parameters('effect')]"
          }
        }
      },
      "id": "/providers/Microsoft.Authorization/policyDefinitions/404c3081-a854-4457-ae30-26a93ef643f9",
      "type": "Microsoft.Authorization/policyDefinitions",
      "name": "404c3081-a854-4457-ae30-26a93ef643f9"
    },
