    {
      "properties": {
        "displayName": "Ensure auto-created NSG rules exist",
        "policyType": "Custom",
        "mode": "All",
        "description": "Ensures that security rules created in auto-created PortLockdown NSGs are not tampered with",
        "metadata": {
          "category": "Port Lockdown"
        },
        "parameters": {
          "nsgPrefix": {
            "type": "String",
            "metadata": {
              "displayName": "NSG name prefix",
              "description": "The prefix string applied to automatically created network security groups."
            }
          },
          "sourceAddressPrefixes": {
            "type": "Array",
            "metadata": {
              "displayName": "Expected IP restriction prefixes",
              "description": "The IP ranges incoming traffic will be restricted to in the expected security rule. I.e. 192.4.0.0/8;192.5.0.0/8 or *"
            }
          },
          "destinationPortRanges": {
            "type": "Array",
            "metadata": {
              "displayName": "Expected destination port ranges",
              "description": "Destination port ranges requiring IP restrictions in the expected security rule"
            }
          },
          "priority": {
            "type": "String",
            "metadata": {
              "displayName": "Expected priority",
              "description": "The priority of the expected security rule."
            }
          },
          "access": {
            "type": "String",
            "metadata": {
              "displayName": "Expected access",
              "description": "The access (allow/deny) of the expected security rule."
            },
            "allowedValues": [
              "Allow",
              "Deny"
            ]
          },
          "name": {
            "type": "String",
            "metadata": {
              "displayName": "Expected name",
              "description": "The name of the expected security rule."
            }
          }
        },
        "policyRule": {
          "if": {
            "allOf": [
              {
                "field": "name",
                "equals": "[concat(parameters('nsgPrefix'), '-', field('location'))]"
              },
              {
                "field": "type",
                "equals": "Microsoft.Network/networkSecurityGroups"
              }
            ]
          },
          "then": {
            "effect": "deployIfNotExists",
            "details": {
              "type": "Microsoft.Network/networkSecurityGroups/securityRules",
              "existenceCondition": {
                "allOf": [
                  {
                    "field": "Microsoft.Network/networkSecurityGroups/securityRules/priority",
                    "equals": "[parameters('priority')]"
                  },
                  {
                    "anyOf": [
                      {
                        "field": "Microsoft.Network/networkSecurityGroups/securityRules/sourceAddressPrefixes[*]",
                        "in": "[parameters('sourceAddressPrefixes')]"
                      },
                      {
                        "field": "Microsoft.Network/networkSecurityGroups/securityRules/sourceAddressPrefix",
                        "in": "[parameters('sourceAddressPrefixes')]"
                      }
                    ]
                  },
                  {
                    "field": "Microsoft.Network/networkSecurityGroups/securityRules/destinationPortRanges[*]",
                    "in": "[parameters('destinationPortRanges')]"
                  },
                  {
                    "field": "Microsoft.Network/networkSecurityGroups/securityRules/access",
                    "equals": "[parameters('access')]"
                  }
                ]
              },
              "deployment": {
                "properties": {
                  "mode": "incremental",
                  "template": {
                    "$schema": "http://schema.management.azure.com/schemas/2015-01-01/deploymentTemplate.json#",
                    "contentVersion": "1.0.0.0",
                    "parameters": {
                      "sourceAddressPrefixes": {
                        "type": "array"
                      },
                      "destinationPortRanges": {
                        "type": "array"
                      },
                      "name": {
                        "type": "string"
                      },
                      "priority": {
                        "type": "string"
                      },
                      "access": {
                        "type": "string"
                      },
                      "nsgName": {
                        "type": "string"
                      }
                    },
                    "variables": {
                      "isSinglePrefix": "[equals(count(parameters('sourceAddressPrefixes')), 1)]",
                      "isSinglePortRange": "[equals(count(parameters('destinationPortRanges')), 1)]"
                    },
                    "resources": [
                      {
                        "name": "[concat(parameters('nsgName'), '/', parameters('name'))]",
                        "type": "Microsoft.Network/networkSecurityGroups/securityRules",
                        "apiVersion": "2018-03-01",
                        "properties": {
                          "description": "Rule auto-created by Internet Exposed Endpoints protection",
                          "protocol": "*",
                          "sourcePortRange": "*",
                          "destinationPortRange": "[if(variables('isSinglePortRange'), first(parameters('destinationPortRanges')), '')]",
                          "destinationPortRanges": "[if(not(variables('isSinglePortRange')), parameters('destinationPortRanges'), json('[]'))]",
                          "sourceAddressPrefix": "[if(variables('isSinglePrefix'), first(parameters('sourceAddressPrefixes')), '')]",
                          "sourceAddressPrefixes": "[if(not(variables('isSinglePrefix')), parameters('sourceAddressPrefixes'), json('[]'))]",
                          "destinationAddressPrefix": "*",
                          "access": "[parameters('access')]",
                          "priority": "[int(parameters('priority'))]",
                          "direction": "Inbound"
                        }
                      }
                    ]
                  },
                  "parameters": {
                    "sourceAddressPrefixes": {
                      "value": "[parameters('sourceAddressPrefixes')]"
                    },
                    "destinationPortRanges": {
                      "value": "[parameters('destinationPortRanges')]"
                    },
                    "name": {
                      "value": "[parameters('name')]"
                    },
                    "priority": {
                      "value": "[parameters('priority')]"
                    },
                    "access": {
                      "value": "[parameters('access')]"
                    },
                    "nsgName": {
                      "value": "[field('name')]"
                    }
                  }
                }
              }
            }
          }
        }
      },
