    {
      "properties": {
        "displayName": "testSandipsh metric alert policy1",
        "policyType": "Custom",
        "mode": "All",
        "metadata": {
          "category": "testSandipsh"
        },
        "parameters": {
          "resourceType": {
            "type": "String",
            "metadata": {
              "displayName": "Resource Type",
              "description": "The target resource type."
            }
          },
          "alertNamePrefix": {
            "type": "String",
            "metadata": {
              "displayName": "Alert name prefix",
              "description": "A prefix to be used for the metrics alert."
            }
          },
          "alertDescription": {
            "type": "String",
            "metadata": {
              "displayName": "Alert description.",
              "description": null
            }
          },
          "alertSeverity": {
            "type": "String",
            "metadata": {
              "displayName": "alertSeverity",
              "description": "Severity of alert {0,1,2,3,4}"
            },
            "allowedValues": [
              "0",
              "1",
              "2",
              "3",
              "4"
            ],
            "defaultValue": "3"
          },
          "isEnabled": {
            "type": "String",
            "metadata": {
              "displayName": "isEnabled",
              "description": "Specifies whether the alert is enabled."
            },
            "allowedValues": [
              "true",
              "false"
            ],
            "defaultValue": "true"
          },
          "metricName": {
            "type": "String",
            "metadata": {
              "displayName": "Metric name",
              "description": "Metric name."
            }
          },
          "operator": {
            "type": "String",
            "metadata": {
              "displayName": "Operator",
              "description": "The metric operator."
            }
          },
          "threshold": {
            "type": "String",
            "metadata": {
              "displayName": "Threshold",
              "description": "The threshold."
            }
          },
          "timeAggregation": {
            "type": "String",
            "metadata": {
              "displayName": "TimeAggregation",
              "description": "The timeAggregation."
            }
          },
          "windowSize": {
            "type": "String",
            "metadata": {
              "displayName": "Window size",
              "description": "The window size."
            }
          },
          "evaluationFrequency": {
            "type": "String",
            "metadata": {
              "displayName": "Evaluation Frequency",
              "description": "The evaluation frequency."
            }
          },
          "actionGroupId": {
            "type": "String",
            "metadata": {
              "displayName": "ActionGroupId",
              "description": "The action group id."
            }
          }
        },
        "policyRule": {
          "if": {
            "field": "type",
            "equals": "[parameters('resourceType')]"
          },
          "then": {
            "effect": "DeployIfNotExists",
            "details": {
              "type": "Microsoft.Insights/metricAlerts",
              "existenceCondition": {
                "field": "name",
                "like": "[concat(parameters('alertNamePrefix'), '*')]"
              },
              "existenceScope": "subscription",
              "deployment": {
                "properties": {
                  "mode": "incremental",
                  "template": {
                    "$schema": "http://schema.management.azure.com/schemas/2015-01-01/deploymentTemplate.json#",
                    "contentVersion": "1.0.0.0",
                    "parameters": {
                      "alertNamePrefix": {
                        "type": "string",
                        "minLength": 1,
                        "metadata": {
                          "description": "A prefix to be used for the metrics alert."
                        }
                      },
                      "alertDescription": {
                        "type": "string",
                        "defaultValue": "This is a metric alert",
                        "metadata": {
                          "description": "Description of alert"
                        }
                      },
                      "alertSeverity": {
                        "type": "string",
                        "defaultValue": "3",
                        "allowedValues": [
                          "0",
                          "1",
                          "2",
                          "3",
                          "4"
                        ],
                        "metadata": {
                          "description": "Severity of alert {0,1,2,3,4}"
                        }
                      },
                      "isEnabled": {
                        "type": "string",
                        "defaultValue": "true",
                        "metadata": {
                          "description": "Specifies whether the alert is enabled"
                        }
                      },
                      "metricName": {
                        "type": "string",
                        "minLength": 1,
                        "metadata": {
                          "description": "Name of the metric used in the comparison to activate the alert."
                        }
                      },
                      "operator": {
                        "type": "string",
                        "defaultValue": "GreaterThan",
                        "allowedValues": [
                          "Equals",
                          "NotEquals",
                          "GreaterThan",
                          "GreaterThanOrEqual",
                          "LessThan",
                          "LessThanOrEqual"
                        ],
                        "metadata": {
                          "description": "Operator comparing the current value with the threshold value."
                        }
                      },
                      "threshold": {
                        "type": "string",
                        "defaultValue": "0",
                        "metadata": {
                          "description": "The threshold value at which the alert is activated."
                        }
                      },
                      "timeAggregation": {
                        "type": "string",
                        "defaultValue": "Average",
                        "allowedValues": [
                          "Average",
                          "Minimum",
                          "Maximum",
                          "Total"
                        ],
                        "metadata": {
                          "description": "How the data that is collected should be combined over time."
                        }
                      },
                      "windowSize": {
                        "type": "string",
                        "defaultValue": "PT5M",
                        "metadata": {
                          "description": "Period of time used to monitor alert activity based on the threshold. Must be between five minutes and one day. ISO 8601 duration format."
                        }
                      },
                      "evaluationFrequency": {
                        "type": "string",
                        "defaultValue": "PT1M",
                        "metadata": {
                          "description": "how often the metric alert is evaluated represented in ISO 8601 duration format"
                        }
                      },
                      "actionGroupId": {
                        "type": "string",
                        "defaultValue": "",
                        "metadata": {
                          "description": "The ID of the action group that is triggered when the alert is activated or deactivated"
                        }
                      },
                      "resourceType": {
                        "type": "string",
                        "metadata": {
                          "description": "The resource type of target resource."
                        }
                      },
                      "targetResourceName": {
                        "type": "string",
                        "metadata": {
                          "description": "The target resource name."
                        }
                      }
                    },
                    "variables": {
                      "targetResourceType": "[parameters('resourceType')]",
                      "resourceIdentifier": "[resourceId(variables('targetResourceType'), parameters('targetResourceName'))]",
                      "alertName": "[concat(toLower(parameters('alertNamePrefix')), uniqueString(resourceGroup().id))]"
                    },
                    "resources": [
                      {
                        "name": "[variables('alertName')]",
                        "type": "Microsoft.Insights/metricAlerts",
                        "location": "global",
                        "apiVersion": "2018-03-01",
                        "tags": {},
                        "properties": {
                          "description": "[parameters('alertDescription')]",
                          "severity": "[parameters('alertSeverity')]",
                          "enabled": "[parameters('isEnabled')]",
                          "scopes": [
                            "[variables('resourceIdentifier')]"
                          ],
                          "evaluationFrequency": "[parameters('evaluationFrequency')]",
                          "windowSize": "[parameters('windowSize')]",
                          "criteria": {
                            "odata.type": "Microsoft.Azure.Monitor.SingleResourceMultipleMetricCriteria",
                            "allOf": [
                              {
                                "name": "1st criterion",
                                "metricName": "[parameters('metricName')]",
                                "dimensions": [],
                                "operator": "[parameters('operator')]",
                                "threshold": "[parameters('threshold')]",
                                "timeAggregation": "[parameters('timeAggregation')]"
                              }
                            ]
                          },
                          "actions": [
                            {
                              "actionGroupId": "[parameters('actionGroupId')]"
                            }
                          ]
                        }
                      }
                    ]
                  },
                  "parameters": {
                    "alertNamePrefix": {
                      "value": "[parameters('alertNamePrefix')]"
                    },
                    "alertDescription": {
                      "value": "[parameters('alertDescription')]"
                    },
                    "alertSeverity": {
                      "value": "[parameters('alertSeverity')]"
                    },
                    "isEnabled": {
                      "value": "[parameters('isEnabled')]"
                    },
                    "metricName": {
                      "value": "[parameters('metricName')]"
                    },
                    "operator": {
                      "value": "[parameters('operator')]"
                    },
                    "threshold": {
                      "value": "[parameters('threshold')]"
                    },
                    "timeAggregation": {
                      "value": "[parameters('timeAggregation')]"
                    },
                    "windowSize": {
                      "value": "[parameters('windowSize')]"
                    },
                    "evaluationFrequency": {
                      "value": "[parameters('evaluationFrequency')]"
                    },
                    "actionGroupId": {
                      "value": "[parameters('actionGroupId')]"
                    },
                    "resourceType": {
                      "value": "[parameters('resourceType')]"
                    },
                    "targetResourceName": {
                      "value": "[field('name')]"
                    }
                  }
                }
              }
            }
          }
        }
      },
